  `keywords` varchar(200) NOT NULL,
  `ban_img_url` varchar(200) NOT NULL,
  `link0` varchar(30) NOT NULL,
  `link1` varchar(30) NOT NULL,
  `link2` varchar(30) NOT NULL,
  `link3` varchar(30) NOT NULL,
  `link4` varchar(30) NOT NULL,
  `link5` varchar(30) NOT NULL,
  `link6` varchar(30) NOT NULL,
  `link7` varchar(30) NOT NULL,
  `title` varchar(200) NOT NULL,
  `main_text` text NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

